CREATE TABLE IF NOT EXISTS `PREFIX_connect` (`CNT_CUST_ID` INT UNSIGNED NOT NULL DEFAULT '0', `CNT_SHOP_ID` TINYINT UNSIGNED NOT NULL DEFAULT '1', `CNT_CUST_TYPE` ENUM('none','facebook', 'twitter', 'google', 'paypal', 'amazon') DEFAULT 'none', `CNT_CUST_SOCIAL_ID` VARCHAR(255) NOT NULL DEFAULT '0', PRIMARY KEY (`CNT_CUST_ID`,`CNT_SHOP_ID`, `CNT_CUST_TYPE`), INDEX `X_BTR_SOCIAL` (`CNT_CUST_SOCIAL_ID`, `CNT_CUST_TYPE`), INDEX `X_BTR_TYPE` (`CNT_CUST_TYPE`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_customer_assoc` (`CCA_CUST_ID` INT UNSIGNED NOT NULL DEFAULT '0', `CCA_SHOP_ID` TINYINT UNSIGNED NOT NULL DEFAULT '1', PRIMARY KEY (`CCA_CUST_ID`,`CCA_SHOP_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_collect` (`COL_ID` INT UNSIGNED NOT NULL auto_increment , `COL_CUST_ID` INT UNSIGNED NOT NULL DEFAULT '0', `COL_SOCIAL_ID` VARCHAR(255) NOT NULL DEFAULT '0', `COL_SHOP_ID` TINYINT UNSIGNED NOT NULL DEFAULT '1', `COL_SOCIAL_TYPE` ENUM('facebook', 'twitter', 'google', 'paypal', 'amazon') DEFAULT 'facebook', `COL_OBJ_ID` INT UNSIGNED NOT NULL DEFAULT '0', `COL_ACTION` CHAR(20) NOT NULL DEFAULT 'like', `COL_TYPE` CHAR(30) NOT NULL DEFAULT 'product', `COL_DATE_ADD` timestamp NULL, PRIMARY KEY (`COL_ID`), UNIQUE KEY(`COL_CUST_ID`,`COL_SOCIAL_ID`,`COL_SHOP_ID`,`COL_SOCIAL_TYPE`,`COL_OBJ_ID`,`COL_ACTION`), INDEX `X_BTR_COLLECT` (`COL_SHOP_ID`,`COL_SOCIAL_ID`,`COL_CUST_ID`,`COL_SOCIAL_TYPE`,`COL_ACTION`,`COL_OBJ_ID`), INDEX `X_BTR_SOCIAL_ID` (`COL_SOCIAL_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
