		if(!empty($android_key) && !empty($devices) && !empty($pushparams[$GLOBALS["dTgTlqxkJVxCEotaParH"]]) && !empty($pushparams[$GLOBALS["lfZwhCoVRiYhvVPBQbEe"]]))
		{
			$msg = array(
				'message'  => $pushparams['message'],
				'title'    => $pushparams['heading'],
				'deeplink' => $pushparams['deeplink'],
				'imageurl' => $pushparams['image_url'],
