			$androidDevices = array_chunk($devices, 1000);

			foreach($androidDevices as $adevices)
			{
				$fields = array(
					'registration_ids' => $adevices,
					'data'             => $msg
				);
				
				$ch = curl_init();
				curl_setopt($ch, CURLOPT_URL, $GLOBALS["oLRPeaYkYjxFcQKguetc"] );
				curl_setopt($ch, CURLOPT_POST, true);
				curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
				curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
				curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
				curl_setopt($ch, CURLOPT_POSTFIELDS, Tools::jsonEncode($fields));
				$result = curl_exec($ch);
				curl_close($ch);

				$log[] = $GLOBALS["oKKxDcPADDFDhDrujQYY"];
				$log[] = $msg;
				$log[] = $GLOBALS["cHkzcRCwKRFHwHAoRQQo"];
				$log[] = $adevices;
				$log[] = $GLOBALS["gQMoGKwxjPAfCZFPjJoS"];
				$log[] = $result;
			}
		}
		$this->printPushLog($log);
	}

