    public function getStoreInfo()
    {
        $info = array();
        $info['currencies'] = $this->getCurrencies();
        $info['languages'] = $this->getLanguages();
        return $info;
    }

    
    public function getLanguages()
    {
        $info = array();
        $position = 0;
        $languages = Language::getLanguages();

        foreach ($languages as $language) {
            if($language[$GLOBALS["pfcfJhkXuTCKuuGHHo"]] == $GLOBALS["SHlRCNLzGtCGyHhhetLl"]) {
                $_info = array(
                    'base_url'   => '',
                    'website_id' => '1',
                    'group_id'   => $language['id_lang'],
                    'store_id'   => $language['id_lang'],
                    'name'       => $language['name'],
                    'sort_order' => $position,
                    'is_active'  => $language['active'],
                    'code'       => $language['iso_code'],
                    'config'     => array(
                        'default_country'            => '',
                        'display_state'              => '',
                        'web_secure_use_in_frontend' => '0',
                        'web_url_use_store'          => '0',
                        'no_image_url'               => '',
                        ),
                    $GLOBALS["fQbSIEoRHpUwdCtcgFay"]         => (string)Category::getRootCategory()->id,
                    $GLOBALS["eAoyLBrzMbviajqKrxHw"]               => $language[$GLOBALS["BoWuBfavKqJxeLgCuLA"]],
                    $GLOBALS["bvDVgfESzkQlKkimxINs"] => $this->getLanguageCurrencies(),
                );

                if($language[$GLOBALS["zjPHoqMrxQVSthrdUTRq"]]) {
                    $_info[$GLOBALS["DsYvyGGkZYZagMLCPqaF"]] = $GLOBALS["SHlRCNLzGtCGyHhhetLl"];
                }

                $currencies = array();
                $currencyResult = Db::getInstance(_PS_USE_SQL_SLAVE_)->executeS($GLOBALS["ntvWYDkHdSMrqVfIXXYr"]._DB_PREFIX_.$GLOBALS["WPSmpBRnRXEnTxcqdcsQ"]);
                if($currencyResult){
                    foreach($currencyResult as $currencyRow){
                        $currency = new Currency($currencyRow[$GLOBALS["xlhZIDgKOCIUhPpCPieU"]]);
                        $currencies[] = array(
                            'name'   => $currency->name,
                            'symbol' => $currency->sign,
                            'code'   => $currency->iso_code
                            );
                    }
                }
                $_info[$GLOBALS["vcDzuvzvvrlhODcEtXLY"]] = $currencies;

                if($language[$GLOBALS["waWafEeALmeXuRDBxZD"]] == $this->context->cookie->id_lang) {
                    $currency = new Currency($this->context->cookie->id_currency);
                    $_info[$GLOBALS["asxETQgoYgCVOHXhnboY"]] = array(
                        'name'   => $currency->name,
