                'currency_symbol'       => $currency['sign'],
                'currency_symbol_right' => $format & 0x01 ? false : true,
                'decimal_symbol'        => $format % 3 == 1 ? '.' : ',',
                'group_symbol'          => ($currency['blank'] ? ' ' : ($format % 3 == 1 ? ',' : '.')),
