    public function checkAddressIntegrity($address)
    {
        if (empty($address) || !is_array($address))
        {
            return false;
        }

        $addressFields = $this->getAddressFields();
        foreach ($addressFields as $field)
        {
            if ($field[$GLOBALS["NfjjcVEGrlZrcaNSlODH"]] === true)
            {
                $name = $field[$GLOBALS["kqpjokinrNKzMlmecrBs"]];
                if ($name == $GLOBALS["mrYkxwAvScQSssyfGPpw"]) {
                    if (!isset($address[$GLOBALS["GNgmbRbejfRAIZpcZGBR"]]) || empty($address[$GLOBALS["GNgmbRbejfRAIZpcZGBR"]]))
                    {
                        return false;
                    }
                }
                elseif ($name == $GLOBALS["zFKuBzQBxOBRHwyZJvte"])
                {
                    if (isset($address[$GLOBALS["NRdTFiuguKOcyboYWuHE"]]) && intval($address[$GLOBALS["NRdTFiuguKOcyboYWuHE"]]))
                    {
                        continue;
                    }
                    else if (isset($address[$GLOBALS["GNvpafcVjyBCEkcYYRA"]]) && $address[$GLOBALS["GNvpafcVjyBCEkcYYRA"]])
                    {
                        continue;
                    }
                    else if (isset($address[$GLOBALS["cVXWbEUBKseJtcmtmElA"]]) && $address[$GLOBALS["cVXWbEUBKseJtcmtmElA"]])
                    {
                        continue;
                    }
                    else
                    {
                        return false;
                    }
                }
                else if($name == $GLOBALS["WKBChtIeVLMffdOLPBQn"])
                {
                    if (isset($address[$GLOBALS["AqCeTptAbNTIqoCPADtH"]]) && intval($address[$GLOBALS["AqCeTptAbNTIqoCPADtH"]]))
                    {
                        continue;
                    }
                    else if (isset($address[$GLOBALS["WKBChtIeVLMffdOLPBQn"]]) && $address[$GLOBALS["WKBChtIeVLMffdOLPBQn"]])
                    {
                        continue;
                    }
                    else
                    {
                        return false;
                    }
                }
                elseif(!isset($address[$name]) || empty($address[$name]))
                {
                    return false;
                }
            }
        }

        return true;
    }

    
