/*jshint unused:false */
/*global tinymce:true */

/**
 * Example plugin that adds a toolbar button and menu item.
 */
tinymce.PluginManager.add('shortcode', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('shortcode', {
        text: 'Short Code',
        icon: false,
        image : url + '/dropcap.jpg',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Smart Short Code plugin',
                body: [
                    {type: 'textbox', name: 'title', label: 'Title'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent('Title: ' + e.data.title);
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('shortcode', {
        text: 'Example plugin',
        context: 'tools',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'TinyMCE site',
                url: url +  '/tinymce_shortcodes.php',
                width: 600,
                height: 400,
                buttons: [
                    {
                        text: 'Insert',
                        onclick: function() {
                            // Top most window object
                            var win = editor.windowManager.getWindows()[0];

                            // Insert the contents of the dialog.html textarea into the editor
                            editor.insertContent(win.getContentWindow().document.getElementById('content').value);

                            // Close the window
                            win.close();
                        }
                    },

                    {text: 'Close', onclick: 'close'}
                ]
            });
        }
    });
});