/* Adapted from http://brettterpstra.com/adding-a-tinymce-button/ */

(function() {
    tinyMCE.create('tinymce.plugins.bootstrap', {
        init : function(ed, url) {
            
            ed.addButton('bootstrap', {
                title : 'BootStrap',              
                image : url + '/dropcap.jpg',
                
                onclick : function() {

					ed.windowManager.open({
                        
                        title: 'Smart BootStrap ShortCode Module',

						file : url + '/bootstrap.php',

						width : 1020, //300

						height : 514, //150

						inline : 1

					});
	}
            });
 

        },
        // ... Hidden code
    });
    // Register plugin
    tinyMCE.PluginManager.add( 'bootstrap', tinyMCE.plugins.bootstrap );
})();