/* Adapted from http://brettterpstra.com/adding-a-tinymce-button/ */

(function() {
    tinyMCE.create('tinymce.plugins.fontawesome', {
        init : function(ed, url) {
            
            ed.addButton('fontawesome', {
                title : 'Font Awesome',
              
                image : url + '/dropcap.jpg',
                
                onclick : function() {

					ed.windowManager.open({
                        
                        title: 'Smart Font Awesome ShortCode Module',

						file : url + '/font-awesome.html',

						width : 700,

						height : 600,

						inline : 1

					});
	}
            });
 

        },
        // ... Hidden code
    });
    // Register plugin
    tinyMCE.PluginManager.add( 'fontawesome', tinyMCE.plugins.fontawesome );
})();